let defaults = {
    temperature: 40,
    noise: 90,
    humidity: 80,
    traffic: 90,
    aqi: 200
};

let stored = flow.get("thresholds") || {};
let thresholds = { ...defaults, ...stored };

// Determina il tipo di "sensore"
let sensor;

// Caso 1: messaggi dai sensori grezzi (dopo parse topic)
if (msg.sensor) {
    sensor = msg.sensor;
}
// Caso 2: messaggi AQI (topic: smartcity/processed/.../aqi)
else if (msg.topic && msg.topic.endsWith("/aqi")) {
    sensor = "aqi";
    msg.sensor = "aqi"; // normalizziamo il messaggio
} else {
    return null;
}

// Se non abbiamo una soglia per questo sensore, ignora
if (!thresholds.hasOwnProperty(sensor)) {
    return null;
}

msg.threshold = thresholds[sensor];
return msg;

let value = msg.payload.value;

// Ricava location in modo robusto
let district = msg.district;
let street = msg.street;

// Se arriva dall'AQI, district/street non esistono: estrai dal topic
if (!district || !street) {
    let parts = msg.topic.split("/");
    // smartcity/processed/district_1/street_0/aqi
    district = parts[2];
    street = parts[3];
}

let location = `${district}/${street}`;

msg.payload = {
    chatId: Number(env.get("TELEGRAM_CHATID")),
    type: "message",
    content:
`ðŸš¨ ALERT

Domain: Smart City IoT
Sensor: ${msg.sensor}
Location: ${location}
Condition: Exceeds ${msg.sensor} threshold: ${value} > ${msg.threshold}`
};

return msg;

if (!Array.isArray(msg.payload) || msg.payload.length === 0) {
    return null;
}

// struttura: { "district/street": { co, pm25, pm10, no2, o3 } }
let map = {};

// costruisce la mappa dai record Influx
for (let r of msg.payload) {
    const key = r.district + "/" + r.street;

    if (!map[key]) map[key] = {};

    map[key][r.sensor] = r._value;
}

// per ogni strada che ha tutti i sensori aria, calcola AQI
let out = [];

for (let key in map) {
    let s = map[key];

    if (
        s.pm25 !== undefined &&
        s.pm10 !== undefined &&
        s.co   !== undefined &&
        s.no2  !== undefined &&
        s.o3   !== undefined
    ) {
        let aqi_pm25 = s.pm25 * 2;
        let aqi_pm10 = s.pm10;
        let aqi_co   = s.co * 20;
        let aqi_no2  = s.no2 / 2;
        let aqi_o3   = s.o3 / 2;

        let aqi = Math.round(Math.max(
            aqi_pm25, aqi_pm10, aqi_co, aqi_no2, aqi_o3
        ));

        let level =
            aqi < 50  ? "good" :
            aqi < 100 ? "moderate" :
            aqi < 150 ? "unhealthy" : "danger";

        let [district, street] = key.split("/");

        out.push({
            district,
            street,
            sensor: "aqi",
            payload: {
                value: aqi,
                level,
                timestamp: Date.now()
            }
        });
    }
}

return [out];

if (!Array.isArray(msg.payload) || msg.payload.length === 0) {
    return null;
}

const allowed = ["temperature", "noise", "traffic", "humidity"];
let out = [];

for (let r of msg.payload) {
    if (!allowed.includes(r.sensor)) continue;

    out.push({
        district: r.district,
        street: r.street,
        sensor: r.sensor,
        payload: {
            value: r._value,
            timestamp: new Date(r._time).getTime()
        }
    });
}

return [out];
